changeSet.createProcedure:
CREATE OR REPLACE FUNCTION getContextId(ctx varchar(64))
RETURNS BIGINT
AS $$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.CONFIG_CONTEXTS where CONTEXT = ctx;
    RETURN result;
    END;
$$
LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION getFieldId(key varchar(64))
RETURNS BIGINT
AS $$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result from ALERT.DEFINED_FIELDS
        where SOURCE_KEY = key;
    RETURN result;
    END;
$$
LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION getDescriptorId(dName varchar(64))
RETURNS BIGINT
AS $$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.REGISTERED_DESCRIPTORS where NAME = dName;
    RETURN result;
    END;
$$
LANGUAGE plpgsql;

changeSet.sql:
insert into ALERT.CONFIG_CONTEXTS (CONTEXT)
select ('GLOBAL')
where not exists (
    select * from ALERT.CONFIG_CONTEXTS where CONTEXT = 'GLOBAL'
);

insert into ALERT.CONFIG_CONTEXTS (CONTEXT)
select ('DISTRIBUTION')
where not exists (
    select * from ALERT.CONFIG_CONTEXTS where CONTEXT = 'DISTRIBUTION'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
select ('CHANNEL')
where not exists (
    select * from ALERT.DESCRIPTOR_TYPES where type = 'CHANNEL'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
select ('PROVIDER')
where not exists (
    select * from ALERT.DESCRIPTOR_TYPES where type = 'PROVIDER'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
select ('COMPONENT')
where not exists (
    select * from ALERT.DESCRIPTOR_TYPES where type = 'COMPONENT'
);
