changeSet.createProcedure:
CREATE OR REPLACE FUNCTION GET_CONTEXT_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.CONFIG_CONTEXTS where CONTEXT = $1;
        RETURN result;
    END;
$result$
LANGUAGE plpgsql;

CREATE ALIAS GET_CONTEXT_ID AS $$
    java.lang.Integer getContextId(final java.sql.Connection connection, final java.lang.String context) throws java.sql.SQLException {
        try (final java.sql.Statement statement = connection.createStatement()) {
            final java.sql.ResultSet resultSet = statement.executeQuery("SELECT ID FROM ALERT.CONFIG_CONTEXTS WHERE CONFIG_CONTEXTS.CONTEXT = '" + context.toUpperCase() + "' LIMIT 1;");
            if (resultSet.next()) {
                return resultSet.getInt("ID");
            } else {
                throw new java.sql.SQLException("No values returned for that query");
            }
        }
    }
$$;

CREATE OR REPLACE FUNCTION GET_FIELD_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result from ALERT.DEFINED_FIELDS
        where SOURCE_KEY = $1;
        RETURN result;
    END;
$result$
LANGUAGE plpgsql;

CREATE ALIAS GET_FIELD_ID AS $$
    java.lang.Integer getFieldId(final java.sql.Connection connection, final java.lang.String sourceKey) throws java.sql.SQLException {
        try (final java.sql.Statement statement = connection.createStatement()) {
            final java.sql.ResultSet resultSet = statement.executeQuery("SELECT ID FROM ALERT.DEFINED_FIELDS WHERE DEFINED_FIELDS.SOURCE_KEY = '" + sourceKey + "' LIMIT 1;");
            if (resultSet.next()) {
                return resultSet.getInt("ID");
            } else {
                throw new java.sql.SQLException("No values returned for that query");
            }
        }
    }
$$;

