CREATE OR REPLACE FUNCTION GET_DESCRIPTOR_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.REGISTERED_DESCRIPTORS where NAME = $1;
    RETURN result;
    END;
$result$
LANGUAGE plpgsql;

CREATE ALIAS GET_DESCRIPTOR_ID AS $$
    java.lang.Integer getDescriptorId(final java.sql.Connection connection, final java.lang.String descriptorName) throws java.sql.SQLException {
        try (final java.sql.Statement statement = connection.createStatement()) {
            final java.sql.ResultSet resultSet = statement.executeQuery("SELECT ID FROM ALERT.REGISTERED_DESCRIPTORS WHERE REGISTERED_DESCRIPTORS.NAME = '" + descriptorName.toLowerCase() + "' LIMIT 1;");
            if (resultSet.next()) {
                return resultSet.getInt("ID");
            } else {
                throw new java.sql.SQLException("No values returned for that query");
            }
        }
    }
$$;

changeSet.sql:
insert into ALERT.CONFIG_CONTEXTS (CONTEXT)
select ('GLOBAL')
where not exists (
    select * from ALERT.CONFIG_CONTEXTS where CONTEXT = 'GLOBAL'
);

insert into ALERT.CONFIG_CONTEXTS (CONTEXT)
select ('DISTRIBUTION')
where not exists (
    select * from ALERT.CONFIG_CONTEXTS where CONTEXT = 'DISTRIBUTION'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
select ('CHANNEL')
where not exists (
    select * from ALERT.DESCRIPTOR_TYPES where type = 'CHANNEL'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
